    window.SC.renderLoginStatus = function() {
      var $p = document.getElementById('current-plan-badge')
        , $h = document.getElementById('current-user-head')
        , $u = document.getElementById('logged-in-user')
        , $ht = document.getElementsByTagName('html')[0]
        , $b = document.getElementsByTagName('body')[0]
        , w = $b.getAttribute('data-sc-owner')
        , c = document.cookie.split(';')
        , u = {}
        , o = {};

      for(var i = 0; i < c.length; i++) {
        var pair = c[i].split('=');
        o[pair[0].replace(/^\s/, '')] = decodeURIComponent(pair[1]).replace(/\+/g, ' ');
      }

      if($h) {
        $h.innerHTML = '';
        $h.appendChild(document.createTextNode(o['u'] || 'stranger'));
        $h.href = '/' + o['p'];
      }

      if($ht) {
        $ht.className = $ht.className.replace(/ ?(anonymous|logged-in)/g, '');
        $ht.className += o['p'] ? ' logged-in' : ' anonymous';
      }

      u.authenticityToken = o['k']
      u.avatarTiny = o['a'];
      u.permalink = o['p'];
      u.plan = o['l'];
      u.planName = o['c'];
      u.tracks_count = o['r'];
      u.comments_count = o['o'];
      u.favorites_count = o['v'];
      u.id = parseInt(o['i']);

      if(o['t']) {
        u.types = o['t'].split('&');
      } else {
        u.types = [];
      }

      u.quota = {};
      u.quota.playlists = o['qp'];
      u.quota.sharings_per_track = o['qs'];
      u.quota.contributions_per_track = o['qc'];
      u.quota.contact_groupings = o['qn'];
      u.quota.groups = o['qg'];
      u.quota.support = o['qt'];

      if(o['c'] === 'free') {
        if($u) { $u.className = 'free-user'; }
        if($p) { $p.className += ' hidden' }
      } else {
        if($p) { $p.className += ' plan-' + o['c']; }
      }

      if($b && w && (w === u.permalink)) {
        $b.className += ' current-user';
      }

      window.SC.currentUser = u;
    };

    window.SC.renderSupportLevel = function() {
      var support = 'Support';

      if (SC.currentUser.quota.support === 'premium') {
        support = 'Premium Support';
      } else if (SC.currentUser.quota.support === 'pro') {
        support = 'Pro Support';
      }
      document.getElementById('footer-support-link').innerHTML = support;
    };

    window.SC.renderLoginStatus();
  //


